section.screen:
Server() { # .. server code here }
Client() { # .. client code here }
if test -z "$JOBID" ; then echo "Variable jobid not set! Assume developer mode" SERVERS="test1.example.com" CLIENTS="test2.example.com" DEVMODE=true fi
if [ -z "$SERVERS" -o -z "$CLIENTS" ]; then echo "Can not determine test type! Client/Server Failed:" RESULT=FAILED report_result $TEST $RESULT fi
if $(echo $SERVERS | grep -q $HOSTNAME); then TEST="$TEST/Server" Server fi
if $(echo $CLIENTS | grep -q $HOSTNAME); then TEST="$TEST/Client" Client fi

#!/bin/sh # Source the common test script helpers . /usr/bin/rhts_environment.sh
# Save STDOUT and STDERR, and redirect everything to a file. exec 5>&1 6>&2 exec >> "${OUTPUTFILE}" 2>&1
client() { echo "-- wait the server to finish." rhts_sync_block -s "DONE" ${SERVERS}
user="finger1" for i in ${SERVERS} do echo "-- finger user \"$user\" from server \"${i}\"." ./finger_client "${i}" "${user}" # It returns non-zero for failure. if [ $? -ne 0 ]; then rhts_sync_set -s "DONE" report_result "${TEST}" "FAIL" 0 exit 1 fi done
echo "-- client finishes." rhts_sync_set -s "DONE" result="PASS" }
server() { # Start server and check it is up and running. /sbin/chkconfig finger on && sleep 5 if ! netstat -a | grep "finger" ; then rhts_sync_set -s "DONE" report_result "${TEST}" "FAIL" 0 exit 1 fi useradd finger1 echo "-- server finishes." rhts_sync_set -s "DONE" rhts_sync_block -s "DONE" ${CLIENTS} result="PASS" }
# ---------- Start Test ------------- result="FAIL" if echo "${CLIENTS}" | grep "${HOSTNAME}" >/dev/null; then echo "-- run finger test as client." TEST=${TEST}/client client fi if echo "${SERVERS}" | grep "${HOSTNAME}" >/dev/null; then echo "-- run finger test as server." TEST=${TEST}/server server fi echo "--- end of runtest.sh." report_result "${TEST}" "${result}" 0 exit 0
